  }
  async getCategoryById(id) {
    const response = await fetch(`http://jservice.io/api/category?id=${id}`);
    const json = await response.json();
    return json;
  }
  async getCategoryById(id) {
    const response = await fetch(`http://jservice.io/api/category?id=${id}`);
    const json = await response.json();
    return json;
  }
  async getInavlid(id) {
    const response = await fetch(`http://jservice.io/api/invalid?id=${id}`);
    const json = await response.json();
    return json;
  }
}

export default new api();
