import React, { Component, createRef } from 'react';
import api from '../../helpers/api';
import Category from './Category';
import Storage from '../../helpers/storage'
import './Category.css';

class CategoryContainer extends Component {
  state = {
    category: null,
    currentQuestion: 0,
    score: 0,
    life: 3,
    answerFalse: "",
    gameOver: "",
  }

  // createRef in order to bring back input value to its parent
  answerInput = createRef();

  // async needed when using promise
  async componentDidMount() {
    const data = await api.getCategoryById(this.props.match.params.id);
    // stored response in the state;
    this.setState({
      category: data,
    });

    const currentStorage = localStorage.getItem('category'+this.state.category.id);
    currentStorage = JSON.parse(currentStorage);
    // On vérifie le cache
    if(currentStorage != null){
      this.setState({
      })
    }
  }

  // Vérification de la reponse
  handleSubmit = (e) => {
    const { category, currentQuestion, score, life } = this.state;
    const divGameOver = document.getElementById('gameOver');
    const answer = this.answerInput.current.value;

    e.preventDefault();

    console.log(category.clues)

    if(answer === category.clues[currentQuestion].answer) {
      this.setState({
        score: score+1,
        answerFalse: "",
        currentQuestion: currentQuestion+1,
      })

      this.answerInput.current.value = "";

      if(currentQuestion === 4) {
        window.location.replace("/");
      }
    } else {
      this.setState({
        life: life-1,
        answerFalse: "Answer is false"
      })

      if(life === 1) {
        divGameOver.style.display = "initial";
      }
    }
  }

  // Le reset du score 
  resetScore = () => {
    const divGameOver = document.getElementById('gameOver');

    divGameOver.style.display = "none";

    this.setState({
      score: 0,
      life: 3,
      answerFalse: "",
    })
  }

  // 

  render() {
    const { category, currentQuestion, score, life, answerFalse } = this.state;

    if (!category) return <div>is loading</div>

    return (
      <Category
        category={category}
        currentQuestion={category.clues[currentQuestion].question}
        handleSubmit={this.handleSubmit}
        answerInput={this.answerInput}
        resetScore={this.resetScore}
        score={score}
        life={life}
        answerFalse={answerFalse}
        gameOver={this.gameOver}
      />
    );
  }
}

export default CategoryContainer;
