import React from 'react';
import PropTypes from 'prop-types';

const Category = ({ category, currentQuestionIndex, handleSubmit, answerInput, nextQuestion, resetScore, score, error }) => {
  const currentQuestion = category.clues[currentQuestionIndex];

  handleSub = (e) => {
    // here I prevent the default bh of submitting form
    e.preventDefault();
    // write logic to handle good/bad answer
    // increment currentQuestion
    // save in the storage the id of the question
    // if no more question, remove category from categories playable
    // increment score somewhere and redirect to /

    const answer = this.answerInput.current.value;
    // check if answer is equal to the requested answer from the current question
    if(answer === currentQuestion.answer) {
      // On rajoute 1 au score
      console.log("OK")
    } else {
      // On rajoute 1 au nombre d'erreur
      console.log("not ok")
    }
  }

  return (
    <section>
      <form onSubmit={handleSubmit}>
        <h1>You choosed: {category.title}</h1>
        <div className="question">
          <h3 className="question__title">
            {currentQuestion.question}
          </h3>
          <div className="question__answerInput">
            {/* We give the ref below in order check the value */}
            <input ref={answerInput} />
          </div>
          <button className="question__submit" type="submit" onClick={() => nextQuestion()}>
            Next
          </button>
          <button className="" type="submit" onClick={() => resetScore()}>
            reset
          </button>
          <h4>Your score:</h4><p>{score}</p>
          <h4>Errors:</h4><p>{error}</p>
        </div>
      </form>
    </section>
  );
}

Category.propTypes = {
  category: PropTypes.shape({}).isRequired,
  currentQuestionIndex: PropTypes.number.isRequired,
  handleSubmit: PropTypes.func.isRequired,
  answerInput: PropTypes.shape({
    value: PropTypes.instanceOf(HTMLInputElement)
  }),
};

export default Category;
