import React from 'react';
import {
  Link
} from 'react-router-dom';
import PropTypes from 'prop-types';

const Home = ({
  categories
}) => ( < section className = "Showcase" >
  <
  div className = "home" >
  <
  h1 className = "title" > Choose a category < /h1></div >

  {
    categories.length > 0 && ( < section className = "container" > {
        categories.map(category => ( < div className = "content" >
          <
          ul className = "list" >

          <
          Link className = "link"
          to = {
            `/categories/${category.id}`
          }
          key = {
            category.id
          } > {
            category.title
          } < /Link>

          <
          /ul></div >
        ))
      } <
      /section>
    )
  } <
  /section>
);

Home.propTypes = {
  categories: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number,
      title: PropTypes.string,
      clues_count: PropTypes.number
    }),
  ),
}

export default Home;
